sys_script_include.script:
var karimFilter = Class.create();
karimFilter.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getNeededRecords: function(){

		var sysIDs = [];
		var gr = new GlideRecord('incident');
	gr.addEncodedQuery("opened_atONToday@javascript:gs.beginningOfToday()@javascript:gs.endOfToday()");
		gr.query();	

		while(gr.next()){
			sysIDs.push(gr.getUniqueValue());
		}

		return sysIDs;

		},

    type: 'karimFilter'
});
