sysauto_script.script:
var rightNow = new GlideDateTime();
var _24HrsFromNow = new GlideDateTime(rightNow);
_24HrsFromNow.add(86400000);
gs.info("Right Now Variable is "+ rightNow+"\n and the 24 hrs var is "+_24HrsFromNow);
	// Query the database for NeedIt Task records with Due date field values older
	// then the current time.  Only return NeedIt Task records that do not have
	// a State field value of Closed Complete.
	var dueTasks = new GlideRecord('x_58872_needit_needit_task');
	dueTasks.addQuery('due_date','>=',rightNow);
	dueTasks.addQuery('due_date','<=',_24HrsFromNow);
	dueTasks.addQuery('state','!=',3);

	dueTasks.query();
	// Write a log message for each overdue NeedIt Task Record
	while(dueTasks.next()){
		gs.info("dueTasks NeedIt Task record = " + dueTasks.number);
		gs.eventQueue("x_58872_needit.dueSoonNeedItTasks",dueTasks,dueTasks.number,gs.getUserName());
	}
